import { firebaseRef, firebaseAuth } from '../firebase/';
import moment from 'moment';
import { push } from 'react-router-redux';

// - Import action types
import * as types from '../constants/actionTypes';

// - Import actions
import * as globalActions from './globalActions';
import EncryptionAPI from '../api/EncryptionAPI';

/* _____________ CRUD DB _____________ */

/**
 * Log in user in server
 * @param {string} email 
 * @param {string} password 
 */
export var dbLogin = (email, password) => {
    return (dispatch, getState) => {
        dispatch(globalActions.showNotificationRequest());
     
        // Log in user if input matches credentials in db
        return firebaseAuth().signInWithEmailAndPassword(email, password).then((result) => {
            dispatch(globalActions.showNotificationSuccess());
            dispatch(login(result.uid));
            dispatch(push('/'));
        }, (error) => dispatch(globalActions.showErrorMessage(error.code)))
    }
}

/**
 * Login user with OAuth
 * @param {string} type 
 */
export var dbLoginWithOAuth = (provider) => {
    return (dispatch, getState) => {
      dispatch(globalActions.showNotificationRequest());
  
    //   return authorizeService.loginWithOAuth(type).then((result) => {
      return firebaseAuth().signInWithPopup(provider).then((result) => {
        // This gives you a Facebook Access Token. You can use it to access the Facebook API.
        // var token = result.credential.accessToken;
        // The signed-in user info.
        // var user = result.user;
        dispatch(globalActions.showNotificationSuccess());
        dispatch(login(result.uid));
        dispatch(push('/'));
        })
    //   }, (error) => dispatch(globalActions.showErrorMessage(error.code)))
        .catch((error) => {
          // An error happened.
          dispatch(globalActions.showErrorMessage(error.code));
  
        })
    }
  }

// Log out user in server
export var dbLogout = () => {
    return (dispatch, getState) => {
        return firebaseAuth().signOut().then((result) => {
            dispatch(logout());
            dispatch(push('/login'));

        }, (error) => dispatch(globalActions.showErrorMessage(error.code)));
    }
}

/**
 * Register user in database
 * @param {object} user 
 */
export var dbSignup = (user) => {
    return (dispatch, getState) => {
        dispatch(globalActions.showNotificationRequest());
        return firebaseAuth().createUserWithEmailAndPassword(user.email, user.password).then((signupResult) => {
            // Generate keys for encryption
            EncryptionAPI.generateKeys(signupResult.uid);

            // Prevent password from being stored
            delete user.password;
            // Create user signup date in the form: '2018-06-01'
            var date = new Date();
            var monthString = ((date.getMonth() + 1) < 10 ? '0' : '') + (date.getMonth() + 1);
            var dayString = (date.getDate() < 10 ? '0' : '') + date.getDate();
            var dateString = date.getFullYear() + '-' + monthString + '-' + dayString;
            firebaseRef.child(`users/${signupResult.uid}/info`).set({
                ...user,
                avatar: 'noImage',
                memberSince: dateString
            }).then((result) => {
                dispatch(globalActions.showNotificationSuccess())
            }, (error) => dispatch(globalActions.showErrorMessage(error.code)));
            
            dispatch(signup({
                uid: signupResult.uid,
                ...user
            }));
            
            dispatch(push('/'));
        }, (error) => dispatch(globalActions.showErrorMessage(error.code)))
    }

}

/**
 * Change user's password
 * @param {string} newPassword 
 */
export const dbUpdatePassword = (newPassword) => {
    return (dispatch, getState) => {
        dispatch(globalActions.showNotificationRequest());
        firebaseAuth().onAuthStateChanged((user) => {
            if (user) {
                user.updatePassword(newPassword).then(() => {
                    // Update successful.
                    dispatch(globalActions.showNotificationSuccess());
                    dispatch(updatePassword());
                    dispatch(push('/'));
                }, (error) => {
                    // An error happened.
                    switch (error.code) {
                        case 'auth/requires-recent-login':
                            dispatch(globalActions.showErrorMessage(error.code));
                            dispatch(dbLogout());
                            break;
                        default:
                    }
                })
            }

        })
    }
}

/**
 * Reset user's password
 * @param {string} newPassword
 */
export const dbResetPassword = (email) => {
    return (dispatch, getState) => {
      dispatch(globalActions.showNotificationRequest())
  
      return firebaseAuth().sendPasswordResetEmail(email).then(() => {
  
        // Reset password successful.
        dispatch(globalActions.showNotificationSuccess())
        dispatch(push('/login'))
      })
        .catch((error) => {
          // An error happened.
          dispatch(globalActions.showMessage(error.code))
  
        })
    }
  }

/* _____________ CRUD State _____________ */

/**
 * Login user
 * @param {string} uid 
 */
export var login = (uid) => {
    return { type: types.LOGIN, authed: true, uid };
}

/**
 * Logout user
 */
export var logout = () => {
    return { type: types.LOGOUT };
}

/**
 *  Register user
 * @param {object} user 
 */
export var signup = (user) => {
    return {
        type: types.SIGNUP,
        ...user
    };
}

/**
 * Update user's password
 */
export const updatePassword = () => {
    return { type: types.UPDATE_PASSWORD };
}

import firebase, { firebaseRef } from '../firebase/';

// - Import utility components
import moment from 'moment';

// - Import action types
import * as types from '../constants/actionTypes';

// - Import actions
import * as globalActions from './globalActions';
import * as postActions from './postActions';
import * as userActions from './userActions';
import * as notifyActions from './notifyActions';

/* _____________ CRUD DB _____________ */

/**
 * Add a circle
 * @param {string} circleName 
 */
export var dbAddCircle = (circleName) => {
    return (dispatch, getState) => {
        let uid = getState().authorize.uid;
        let circle = {
            creationDate: moment().unix(),
            name: circleName,
            users: {}
        };

        let circleRef = firebaseRef.child(`userCircles/${uid}/circles`).push(circle);
        return circleRef.then(() => {
            dispatch(addCircle(uid, {
                ...circle,
                id: circleRef.key
            }));

        }, (error) => dispatch(globalActions.showErrorMessage(error.message)));
    }
}

/**
 * Add a user in a circle
 * @param {string} cid is circle identifier 
 * @param {object} userFollowing is the user for following
 */
export var dbAddFollowingUser = (cid, userFollowing) => {
    return (dispatch, getState) => {
        let uid = getState().authorize.uid;
        let user = getState().user.info[uid];

        let userCircle = {
            creationDate: moment().unix(),
            fullName: userFollowing.fullName,
            avatar: userFollowing.avatar || ''
        };

        let userFollower = {
            creationDate: moment().unix(),
            fullName: user.fullName,
            avatar: user.avatar || '',
            approved: false
        };

        let updates = {};
        updates[`userCircles/${uid}/circles/${cid}/users/${userFollowing.userId}`] = userCircle;
        updates[`userCircles/${userFollowing.userId}/circles/-Followers/users/${uid}`] = userFollower;
        return firebaseRef.update(updates).then((result) => {
            dispatch(addFollowingUser(uid, cid, userFollowing.userId, { ...userCircle }));

            dispatch(notifyActions.dbAddNotify(
                {
                    description: `${user.fullName} follow you.`,
                    url: `/${uid}`,
                    notifyRecieverUserId: userFollowing.userId, notifierUserId: uid,
                    isRequest: false,
                }));

        }, (error) => {
            dispatch(globalActions.showErrorMessage(error.message));
        })
    }
}


/**
 * Delete a user from a circle
 * @param {string} cid is circle identifier 
 * @param {string} followingId following user identifier
 */
export var dbDeleteFollowingUser = (cid, followingId) => {
    return (dispatch, getState) => {
        let uid = getState().authorize.uid;
        let updates = {};
        updates[`userCircles/${uid}/circles/${cid}/users/${followingId}`] = null;
        updates[`userCircles/${followingId}/circles/-Followers/users/${uid}`] = null;
        return firebaseRef.update(updates).then((result) => {
            dispatch(deleteFollowingUser(uid, cid, followingId))
        }, (error) => {
            dispatch(globalActions.showErrorMessage(error.message))
        });
    }
}

/**
 * Update a circle from database
 * @param  {object} newCircle
 */
export const dbUpdateCircle = (newCircle) => {
    return (dispatch, getState) => {
        // Get current user id
        var uid = getState().authorize.uid;

        // Write the new data simultaneously in the list
        let updates = {};
        let circle = getState().circle.userCircles[uid][newCircle.id];
        let updatedCircle = {
            name: newCircle.name || circle.name,
            users: newCircle.users ? newCircle.users : (circle.users || [])
        };

        updates[`userCircles/${uid}/circles/${newCircle.id}`] = updatedCircle;
        return firebaseRef.update(updates).then((result) => {
            dispatch(updateCircle(uid, { id: newCircle.id, ...updatedCircle }))
        }, (error) => {
            dispatch(globalActions.showErrorMessage(error.message))
        });
    }
}


/**
 * Delete a circle from database
 * @param  {string} id is circle identifier
 */
export const dbDeleteCircle = (id) => {
    return (dispatch, getState) => {

        // Get current user id
        var uid = getState().authorize.uid

        // Write the new data simultaneously in the list
        var updates = {};
        updates[`userCircles/${uid}/circles/${id}`] = null;

        return firebaseRef.update(updates).then((result) => {
            dispatch(deleteCircle(uid, id))
        }, (error) => {
            dispatch(globalActions.showErrorMessage(error.message))
        });
    }

}

//  Get all user circles from data base
export const dbGetCircles = () => {
    return (dispatch, getState) => {
        let uid = getState().authorize.uid;
        if (uid) {
            var circlesRef = firebaseRef.child(`userCircles/${uid}/circles`);

            return circlesRef.once('value').then((snapshot) => {
                let circles = snapshot.val() || {};
                let parsedCircles = {};
                Object.keys(circles).forEach((circleId) => {
                    if (circleId !== '-Followers' && circles[circleId].users) {
                        Object.keys(circles[circleId].users).filter((v, i, a) => a.indexOf(v) === i).forEach((userId) => {
                            dispatch(postActions.dbGetPostsByUserId(userId));
                            dispatch(userActions.dbGetUserInfoByUserId(userId));
                        })
                    }

                    parsedCircles[circleId] = {
                        id: circleId,
                        ...circles[circleId]
                    };
                })

                dispatch(addCircles(uid, parsedCircles));;
            });
        }
    }
}


//  Get all user circles from data base by user id
export const dbGetCirclesByUserId = (uid) => {
    return (dispatch, getState) => {
        if (uid) {
            let circlesRef = firebaseRef.child(`userCircles/${uid}/circles`);

            return circlesRef.once('value').then((snapshot) => {
                let circles = snapshot.val() || {};
                let parsedCircles = {};
                Object.keys(circles).forEach((circleId) => {
                    parsedCircles[circleId] = {
                        id: circleId,
                        ...circles[circleId]
                    };
                })
                dispatch(addCircles(uid, parsedCircles));
            })
        }
    }
}

/* _____________ CRUD State _____________ */

/**
 * Add a normal circle
 * @param {string} uid is user identifier
 * @param {object} circle 
 */
export const addCircle = (uid, circle) => {
    return {
        type: types.ADD_CIRCLE,
        payload: { uid, circle }
    };
}

/**
 * Update a circle
 * @param {string} uid is user identifier
 * @param {object} circle 
 */
export const updateCircle = (uid, circle) => {
    return {
        type: types.UPDATE_CIRCLE,
        payload: { uid, circle }
    };
}

/**
 * Delete a circle
 * @param {string} uid is user identifier
 * @param {string} id is circle identifier
 */
export const deleteCircle = (uid, id) => {
    return {
        type: types.DELETE_CIRCLE,
        payload: { uid, id }
    };
}


/**
 * Add a list of circle
 * @param {string} uid 
 * @param {[object]} circles 
 */
export const addCircles = (uid, circles) => {
    return {
        type: types.ADD_LIST_CIRCLE,
        payload: { uid, circles }
    };
}

/**
 * Clea all data in circle store
 */
export const clearAllCircles = () => {
    return {
        type: types.CLEAR_ALL_CIRCLES
    };
}


/**
 * Open circle settings
 */
export const openCircleSettings = (uid, id) => {
    return {
        type: types.OPEN_CIRCLE_SETTINGS,
        payload: { uid, id }
    };
}

/**
 * Close open circle settings
 */
export const closeCircleSettings = (uid, id) => {
    return {
        type: types.CLOSE_CIRCLE_SETTINGS,
        payload: { uid, id }
    };
}

/**
 * Add following user in a circle
 * @param {string} uid user identifire who want to follow the following user
 * @param {string} cid circle identifier that following user should be added in
 * @param {string} followingId following user identifier
 * @param {object} userCircle information about following user
 */
export const addFollowingUser = (uid, cid, followingId, userCircle) => {
    return {
        type: types.ADD_FOLLOWING_USER,
        payload: { uid, cid, followingId, userCircle }
    };
}

/**
 * Delete following user from a circle
 * @param {string} uid user identifire who want to follow the following user
 * @param {string} cid circle identifier that following user should be added in
 * @param {string} followingId following user identifier
 */
export const deleteFollowingUser = (uid, cid, followingId) => {
    return {
        type: types.DELETE_FOLLOWING_USER,
        payload: { uid, cid, followingId }
    };
}

import moment from 'moment';
import { firebaseRef } from '../firebase/';

// - Import action types
import * as types from '../constants/actionTypes';

// - Import actions
import * as globalActions from './globalActions';
import * as notifyActions from './notifyActions';

// - Import app API
import EncryptionAPI from '../api/EncryptionAPI';

import forge from 'node-forge';
/* _____________ CRUD DB _____________ */

/**
 *  Add comment to database
 * @param  {object} newComment user comment
 * @param  {function} callBack  will be fired when server responsed
 */
export const dbAddComment = (newComment, callBack) => {
    return (dispatch, getState) => {

        dispatch(globalActions.showTopLoading());

        let uid = getState().authorize.uid;
        let comment = {
            postId: newComment.postId,
            score: 0,
            text: newComment.text,
            creationDate: moment().unix(),
            userDisplayName: getState().user.info[uid].fullName,
            userAvatar: getState().user.info[uid].avatar,
            userId: uid,
        };

        // Deep copy of comments
        let encryptedComment = JSON.parse(JSON.stringify(comment));

        // Get own data key
        let key = localStorage.getItem('dataKey');
        let iv = localStorage.getItem('dataIV');
        let keysRef = firebaseRef.child(`keys/${uid}/${uid}`);
        keysRef.once('value', (snap) => {
            if (snap.val() && (!key || !iv)) {
                let encryptedKey = snap.val().key || '';
                let encryptedIV = snap.val().iv || '';

                // decrypt data key with a private key (defaults to RSAES PKCS#1 v1.5)
                let keyPair = JSON.parse(localStorage.getItem('keyPair'));
                let privateKey = forge.pki.privateKeyFromPem(keyPair.private);
                key = forge.util.decodeUtf8(privateKey.decrypt(encryptedKey));
                iv = forge.util.decodeUtf8(privateKey.decrypt(encryptedIV));
            }

            // Encrypt contents of comments
            encryptedComment.text = EncryptionAPI.encrypt(comment.text, key, iv);

            // Save encrypted comment to firebase
            let commentRef = firebaseRef.child(`postComments/${newComment.postId}`).push(encryptedComment);
            dispatch(addComment(
                {
                    comment,
                    postId: newComment.postId,
                    id: commentRef.key,
                    editorStatus: false,
                }));
            return commentRef.then(() => {
                callBack();
                dispatch(globalActions.hideTopLoading());

                if (newComment.ownerPostUserId !== uid)
                    dispatch(notifyActions.dbAddNotify(
                        {
                            description: 'Add comment on your post.',
                            url: `/${newComment.ownerPostUserId}/posts/${newComment.postId}`,
                            notifyRecieverUserId: newComment.ownerPostUserId, notifierUserId: uid,
                            isRequest: false,
                        }));
            }, (error) => {
                dispatch(globalActions.showErrorMessage(error.message));
                dispatch(globalActions.hideTopLoading());
            })
        })

    }
}

// Get all comments from database
export const dbGetComments = () => {
    return (dispatch, getState) => {
        let uid = getState().authorize.uid;
        if (uid) {
            let commentsRef = firebaseRef.child(`postComments`);
            return commentsRef.on('value', (snapshot) => {
                let comments = snapshot.val() || {};

                // Get reference to all keys in db
                let keysRef = firebaseRef.child(`keys`);
                keysRef.once('value', (keySnapshot) => {
                    let decryptedComments = comments;
                    // For each post, look up its comments
                    Object.keys(comments).forEach((postId) => {
                        // For each comment of a post, decrypt its contents
                        Object.keys(comments[postId]).forEach((commentId) => {
                            // Reference to current comment
                            let currComment = comments[postId][commentId];
                            let keyRef = keySnapshot.val()[uid][currComment.userId] || '';
                            if (keyRef) {
                                // Look up key and iv to decipher comment
                                let encryptedKey = keyRef.key;
                                let encryptedIV = keyRef.iv;

                                // decrypt data with a private key (defaults to RSAES PKCS#1 v1.5)
                                let keyPair = JSON.parse(localStorage.getItem('keyPair'));
                                let privateKey = forge.pki.privateKeyFromPem(keyPair.private);
                                let key = forge.util.decodeUtf8(privateKey.decrypt(encryptedKey));
                                let iv = forge.util.decodeUtf8(privateKey.decrypt(encryptedIV));

                                // Decipher comment
                                decryptedComments[postId][commentId].text = EncryptionAPI.decrypt(currComment.text, key, iv);
                            }
                        });
                    });
                    dispatch(addCommentList(decryptedComments));
                });

            });
        }
    }
}

/**
 * Update a comment from database
 * @param  {string} id of comment
 * @param {string} postId is the identifier of the post which comment belong to
 */
export const dbUpdateComment = (id, postId, text) => {

    return (dispatch, getState) => {

        // Deep copy of comments
        let encryptedText = JSON.parse(JSON.stringify(text));

        // Get own data key
        let key = localStorage.getItem('dataKey');
        let iv = localStorage.getItem('dataIV');
        let keysRef = firebaseRef.child(`keys/${uid}/${uid}`);
        keysRef.once('value', (snap) => {
            if (snap.val() && (!key || !iv)) {
                let encryptedKey = snap.val().key || '';
                let encryptedIV = snap.val().iv || '';

                // decrypt data key with a private key (defaults to RSAES PKCS#1 v1.5)
                let keyPair = JSON.parse(localStorage.getItem('keyPair'));
                let privateKey = forge.pki.privateKeyFromPem(keyPair.private);
                key = forge.util.decodeUtf8(privateKey.decrypt(encryptedKey));
                iv = forge.util.decodeUtf8(privateKey.decrypt(encryptedIV));
            }

            // Encrypt contents of comments
            encryptedText = EncryptionAPI.encrypt(text, key, iv)

            dispatch(globalActions.showTopLoading());

            // Get current user id
            let uid = getState().authorize.uid;

            // Write the new data simultaneously in the list
            let updates = {};
            let comment = getState().comment.postComments[postId][id];

            updates[`postComments/${postId}/${id}`] = {
                postId: postId,
                score: comment.score,
                text: encryptedText,
                creationDate: comment.creationDate,
                userDisplayName: comment.userDisplayName,
                userAvatar: comment.userAvatar,
                userId: uid,
            };

            return firebaseRef.update(updates).then((result) => {
                dispatch(updateComment({ id, postId, text, editorStatus: false }));
                dispatch(globalActions.hideTopLoading());
            }, (error) => {
                dispatch(globalActions.showErrorMessage(error.message));
                dispatch(globalActions.hideTopLoading());
            });
        });
    }

}

/**
 * Delete a comment from database
 * @param  {string} id of comment
 * @param {string} postId is the identifier of the post which comment belong to
 */
export const dbDeleteComment = (id, postId) => {
    return (dispatch, getState) => {
        dispatch(globalActions.showTopLoading());

        // Get current user id
        let uid = getState().authorize.uid;

        // Write the new data simultaneously in the list
        let updates = {};
        updates[`postComments/${postId}/${id}`] = null;

        return firebaseRef.update(updates).then((result) => {
            dispatch(deleteComment(id, postId));
            dispatch(globalActions.hideTopLoading());
        }, (error) => {
            dispatch(globalActions.showErrorMessage(error.message));
            dispatch(globalActions.hideTopLoading());
        });
    }
}

/* _____________ CRUD State _____________ */

/**
 * Add comment 
 * @param {object} data  
 */
export const addComment = (data) => {
    return {
        type: types.ADD_COMMENT,
        payload: data,
    };
}

/**
 * Update comment 
 * @param {object} data  
 */
export const updateComment = (data) => {
    return {
        type: types.UPDATE_COMMENT,
        payload: data,
    };
}

/**
 * Add comment list
 * @param {[object]} postComments an array of comments
 */
export const addCommentList = (postComments) => {
    return {
        type: types.ADD_COMMENT_LIST,
        payload: postComments,
    };
}

/**
 * Delete a comment
 * @param  {string} id of comment
 * @param {string} postId is the identifier of the post which comment belong to
 */
export const deleteComment = (id, postId) => {
    return { type: types.DELETE_COMMENT, payload: { id, postId } };
}

// Clear all data
export const clearAllData = () => {
    return {
        type: types.CLEAR_ALL_DATA_COMMENT,
    };
}

export const openCommentEditor = (comment) => {
    return {
        type: types.OPEN_COMMENT_EDITOR,
        payload: comment,
    };
}

export const closeCommentEditor = (comment) => {
    return {
        type: types.CLOSE_COMMENT_EDITOR,
        payload: comment,
    };
}

import firebase, { firebaseRef } from 'app/firebase/';

// - Import utility components
import moment from 'moment';

// - Import action types
import * as types from 'actionTypes';

// - Import actions
import * as globalActions from 'globalActions';
import * as postActions from './postActions';
import * as userActions from './userActions';
import * as notifyActions from 'notifyActions';

import EncryptionAPI from '../api/EncryptionAPI';

/* _____________ CRUD DB _____________ */


/**
 * Send friend request to a user
 * @param {object} userFriend is the user to send request to
 * */
export var dbAddFriendRequest = (userFriend) => {
    return (dispatch, getState) => {
        let uid = getState().authorize.uid;
        let user = getState().user.info[uid];
        let myRequestId = firebaseRef.child(`userRequests/${uid}/sent`).push().key;
        let theirRequestId = firebaseRef.child(`userRequests/${userFriend.userId}/received`).push().key;
        let timestamp = moment().unix();

        // Store userFriend details 
        let userSentReq = {
            creationDate: timestamp,
            fullName: userFriend.fullName,
            avatar: userFriend.avatar || '',
            approved: false,
            acknowledged: false,
            uid: userFriend.userId,
            reqId: theirRequestId
        };

        // Send your user details to userFriend
        let userReceivedReq = {
            creationDate: timestamp,
            fullName: user.fullName,
            avatar: user.avatar || '',
            uid,
            reqId: myRequestId,
        };

        let updates = {};
        // Track the request on my sent requests branch
        updates[`userRequests/${uid}/sent/${myRequestId}`] = userSentReq;

        // Add to userFriend's request branch
        updates[`userRequests/${userFriend.userId}/received/${theirRequestId}`] = userReceivedReq;
        return firebaseRef.update(updates).then((result) => {
            dispatch(notifyActions.dbAddNotify(
                {
                    description: `${user.fullName} wants to be friends with you.`,
                    url: `/${uid}`,
                    notifyRecieverUserId: userFriend.userId,
                    notifierUserId: uid,
                    isRequest: true,
                }));
        }, (error) => {
            dispatch(globalActions.showErrorMessage(error.message));
        });
    };
}


/**
 * Accept friend request
 * @param string myReqId the id of the received request
 * @param {object} request the received request object
 * */
export var dbAcceptFriendRequest = (myReqId, request) => {
    return (dispatch, getState) => {
        let uid = getState().authorize.uid;

        let userFriend =
            {
                fullName: request.fullName,
                avatar: request.avatar,
                userId: request.uid,
            };
        dispatch(dbAddFriend(userFriend, myReqId, request.reqId, false));
    };
}


export var dbDenyFriendRequest = (myReqId, request) => {
    return (dispatch, getState) => {
        let uid = getState().authorize.uid;

        let userFriend =
            {
                fullName: request.fullName,
                avatar: request.avatar,
                userId: request.uid
            };

        let updates = {};
        updates[`userRequests/${uid}/received/${myReqId}`] = null;
        updates[`userRequests/${userFriend.userId}/sent/${request.reqId}/acknowledged`] = true;
        updates[`userRequests/${userFriend.userId}/sent/${request.reqId}/approved`] = false;
        return firebaseRef.update(updates).then((result) => {

        });
    }
}

/**
* Add a user to friend list
* @param {object} userFriend is the user to add to friend's list
* @param {boolean} requestId unique id of friend request
*/
export var dbAddFriend = (userFriend, myRequestId, theirRequestId, fromUser) => {
    return (dispatch, getState) => {
        let uid = getState().authorize.uid;
        let user = getState().user.info[uid];

        let friend = {
            creationDate: moment().unix(),
            fullName: userFriend.fullName,
            avatar: userFriend.avatar || '',
            uid: userFriend.userId,
        };

        // Encrypt your data key in friend's public key
        EncryptionAPI.sendEncryptedKey(uid, userFriend.userId);

        let updates = {};
        // Check if the friend request was sent by current user
        if (fromUser) {
            // A request that you sent
            updates[`userRequests/${uid}/sent/${myRequestId}`] = null;
        } else {
            // A request which you are responding to
            updates[`userRequests/${userFriend.userId}/sent/${theirRequestId}/approved`] = true;
            updates[`userRequests/${userFriend.userId}/sent/${theirRequestId}/acknowledged`] = true;
            updates[`userRequests/${uid}/received/${myRequestId}`] = null;
        }
        updates[`userFriends/${uid}/${userFriend.userId}`] = friend;
        return firebaseRef.update(updates).then((result) => {
            dispatch(notifyActions.dbAddNotify(
                {
                    description: `${user.fullName} became friends with you.`,
                    url: `/${uid}`,
                    notifyRecieverUserId: userFriend.userId,
                    notifierUserId: uid,
                    isRequest: false,
                }));
        }, (error) => {
            dispatch(globalActions.showErrorMessage(error.message));
        });
    };
}


/**
 * Process the friend request you sent once it was denied
 * @param {object} userFriend is the user whom to cancel the request to
 */
export var dbFriendRequestDenied = (userFriend, myRequestId) => {
    return (dispatch, getState) => {
        let uid = getState().authorize.uid;
        let user = getState().user.info[uid];

        // Remove sent request from self
        let updates = {};
        updates[`userRequests/${uid}/sent/${myRequestId}`] = null;
        return firebaseRef.update(updates).then((result) => {
            // does not notify current user that there request was denied
        }, (error) => {
            dispatch(globalActions.showErrorMessage(error.message));
        });
    };
}

/**
 * Cancel a sent friend request
 * @param {object} userFriend is the user whom to cancel the request to
 */
export var dbCancelFriendRequest = (userFriend, myRequestId, theirRequestId) => {
    return (dispatch, getState) => {
        let uid = getState().authorize.uid;
        let user = getState().user.info[uid];

        // Remove sent request from self and received request from userFriend
        let updates = {};
        updates[`userRequests/${userFriend.userId}/received/${theirRequestId}`] = null;
        updates[`userRequests/${uid}/sent/${myRequestId}`] = null;
        return firebaseRef.update(updates).then((result) => {
        }, (error) => {
