/**
 * Get the circles which the specify users is in that circle 
 * @param {object} circles 
 * @param {string} followingId 
 */
export const getUserBelongCircles = (circles, followingId) => {
    let userBelongCircles = [];
    Object.keys(circles).forEach((cid) => {
        if (cid.trim() !== '-Followers' && circles[cid].users) {
            let isExist = Object.keys(circles[cid].users).indexOf(followingId) > -1;
            if (isExist) {
                userBelongCircles.push(cid);
            }
        }
    });

    return userBelongCircles;
}

/**
 * Get the following users
 * @param {object} circles 
 */
export const getFollowingUsers = (circles) => {
    let followingUsers = {};
    Object.keys(circles).forEach((cid) => {
        if (cid.trim() !== '-Followers' && circles[cid].users) {
            Object.keys(circles[cid].users).forEach((userId) => {
                let isExist = Object.keys(followingUsers).indexOf(userId) > -1;
                if (!isExist) {
                    followingUsers[userId] = {
                        ...circles[cid].users[userId]
                    };
                }
            });
        }
    });

    return followingUsers;
}

export default {
    getUserBelongCircles,
    getFollowingUsers
};

import firebase, { firebaseRef } from '../firebase/';

import forge from 'node-forge';

/**
 * Encrypt message with AES given a key and iv
 * @param {string} plaintext 
 * @param {string} key 
 * @param {string} iv
 * @returns encrypted message
 */
const encrypt = (plaintext, key, iv) => {
    const cipher = forge.cipher.createCipher('AES-CBC', key);
    cipher.start({ iv: iv });
    cipher.update(forge.util.createBuffer(plaintext));
    let pass = cipher.finish();
    return pass ? cipher.output.toHex() : plaintext;
}

/**
 * Decrypt message with AES given a key and iv
 * @param {string} ciphertext
 * @param {string} key 
 * @param {string} iv
 * @returns decrypted message as a String
 */
const decrypt = (ciphertext, key, iv) => {
    let data = forge.util.hexToBytes(ciphertext);
    const decipher = forge.cipher.createDecipher('AES-CBC', key);
    decipher.start({ iv: iv });
    decipher.update(forge.util.createBuffer(data));
    let pass = decipher.finish();
    return pass ? decipher.output.toString() : ciphertext;
}

/**
 * Generate public/private key pair and data key for hybrid encryption
 * @param {string} userId
 */
const generateKeys = (userId) => {

    forge.pki.rsa.generateKeyPair({ bits: 2048, workers: -1 }, (err, keypair) => {
        if (err) {
            console.error(err)
        } else {
            let localStorage = window.localStorage;
            let privateKey = keypair.privateKey;
            let publicKey = keypair.publicKey;

            let keyPair = {
                public: forge.pki.publicKeyToPem(publicKey),
                private: forge.pki.privateKeyToPem(privateKey),
            }
            // Save pair key locally
            localStorage.setItem('keyPair', JSON.stringify(keyPair));

            // Generate a public key for symmetric encryption
            // Note: a key size of 16 bytes will use AES-128, 24 => AES-192, 32 => AES-256
            let key = forge.random.getBytesSync(32);
            let iv = forge.random.getBytesSync(32);
            localStorage.setItem('dataKey', key);
            localStorage.setItem('dataIV', iv);


            // encrypt data with a public key (defaults to RSAES PKCS#1 v1.5)
            let encryptedKey = publicKey.encrypt(forge.util.encodeUtf8(key))
            let encryptedIV = publicKey.encrypt(forge.util.encodeUtf8(iv))

            // store public key and encrypted data key 
            let updates = {};
            updates[`keys/${userId}/${userId}`] = {
                publicKey: keyPair.public,
                key: encryptedKey,
                iv: encryptedIV,
            };
            return firebaseRef.update(updates);
        }
    });
}

/**
 * Send encrypted data key to user added to friend list
 * @param {string} userId is own identifier
 * @param {string} friendId is the identifier of user to send key to
 */
const sendEncryptedKey = (userId, friendId) => {
    return firebaseRef.child(`keys/${friendId}/${friendId}`).once('value', (snapshot) => {
        let publicPEM = snapshot.val().publicKey || '';
        let publicKey = forge.pki.publicKeyFromPem(publicPEM);

        let key = localStorage.getItem('dataKey');
        let iv = localStorage.getItem('dataIV');

        // Encrypt your data key with friend's public key
        let encryptedKey = publicKey.encrypt(forge.util.encodeUtf8(key))
        let encryptedIV = publicKey.encrypt(forge.util.encodeUtf8(iv))

        // Send your encrypted data key to friend
        return firebaseRef.child(`keys/${friendId}/${userId}/`).set({
            key: encryptedKey,
            iv: encryptedIV,
        });
    });
}

export default {
    encrypt,
    decrypt,
    generateKeys,
    sendEncryptedKey,
}

import { storageRef } from '../firebase/';

// - Get file Extension
const getExtension = (fileName) => {
    let re = /(?:\.([^.]+))?$/;
    return re.exec(fileName)[1];
}

// Converts image to canvas; returns new canvas element
const convertImageToCanvas = (image) => {
    let canvas = document.createElement("canvas");
    canvas.width = image.width;
    canvas.height = image.height;
    canvas.getContext("2d").drawImage(image, 0, 0);

    return canvas;
}

/**
 * Upload image on the server
 * @param {file} file 
 * @param {string} fileName 
 */
const uploadImage = (file, fileName, progress) => {
    return new Promise((resolve, reject) => {
        // Create a storage refrence
        let storegeFile = storageRef.child(`images/${fileName}`);

        // Upload file
        let task = storegeFile.put(file);
        task.then((result) => {
            resolve(result);
        }).catch((error) => {
            reject(error);
        });

        // Upload storage bar
        task.on('state_changed', (snapshot) => {
            let percentage = (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
            progress(percentage, true);
        }, (error) => {
            console.log('========== Upload Image ============');
            console.log(error);
            console.log('====================================');
        }, (complete) => {
            progress(100, false);
        });
    });
}

/**
 * Constraint image size
 * @param {file} file 
 * @param {number} maxWidth 
 * @param {number} maxHeight 
 */
const constraintImage = (file, fileName, maxWidth, maxHeight) => {
    // Ensure it's an image
    if (file.type.match(/image.*/)) {
        // Load the image
        let reader = new FileReader();
        reader.onload = function (readerEvent) {
            let image = new Image();
            image.onload = function (imageEvent) {
                // Resize the image
                let canvas = document.createElement('canvas'),
                    max_size = 986,// TODO : pull max size from a site config
                    width = image.width,
                    height = image.height;
                if (width > height) {
                    if (width > max_size) {
                        height *= max_size / width;
                        width = max_size;
                    }
                } else {
                    if (height > max_size) {
                        width *= max_size / height;
                        height = max_size;
                    }
                }
                canvas.width = width;
                canvas.height = height;
                canvas.getContext('2d').drawImage(image, 0, 0, width, height);
                let dataUrl = canvas.toDataURL();
                let resizedImage = dataURLToBlob(dataUrl);
                let evt = new CustomEvent('onSendResizedImage', { detail: { resizedImage, fileName } });
                window.dispatchEvent(evt);
            };

            image.src = readerEvent.target.result;
        };

        reader.readAsDataURL(file);
    }
}

/**
 * Convert data URL to blob
 * @param {object} dataURL 
 */
const dataURLToBlob = (dataURL) => {
    let BASE64_MARKER = ';base64,';

    if (dataURL.indexOf(BASE64_MARKER) == -1) {
        let parts = dataURL.split(',');
        let contentType = parts[0].split(':')[1];
        let raw = parts[1];

        return new Blob([raw], { type: contentType });
    }

    let parts = dataURL.split(BASE64_MARKER);
    let contentType = parts[0].split(':')[1];
    let raw = window.atob(parts[1]);
    let rawLength = raw.length;

    let uInt8Array = new Uint8Array(rawLength);

    for (let i = 0; i < rawLength; ++i) {
        uInt8Array[i] = raw.charCodeAt(i);
    }

    return new Blob([uInt8Array], { type: contentType });
}

export default {
    dataURLToBlob,
    convertImageToCanvas,
    getExtension,
    constraintImage,
    uploadImage
};

// Get tags from post content
export const detectTags = (content, character) => {
    return content.split(" ").filter((word) => {
        return (word.slice(0, 1) === character);
    });
}

export const getContentTags = (content) => {
    let newTags = [];
    let tags = detectTags(content, '#');

    tags.forEach((tag) => {
        newTags.push(tag.slice(1))
    });

    return newTags;
}

export const sortObjectsDate = (objects) => {
    let sortedObjects = objects;

    // Sort posts with creation date
    sortedObjects.sort((a, b) => {
        return parseInt(b.creationDate) - parseInt(a.creationDate);
    });

    return sortedObjects;
}

