// - Import app components
import Blog from './Blog';

// - Import actions
import * as postActions from '../actions/postActions';
import * as userActions from '../actions/userActions';

export class PostPage extends Component {
    
    componentWillMount = () => {
        this.props.loadPost();
        this.props.loadUserInfo();
    }

