/**
 *  Authorize reducer
 * @param {object} state 
 * @param {object} action 
 */
export let authorizeReducer = (state = defaultState, action) => {
    switch (action.type) {
        case types.LOGIN:
            return {
                ...state,
                uid: action.uid,
                authed: true,
                guest: false
            };
        case types.LOGOUT:
            return {
                ...state,
                uid: 0,
                authed: false,
                guest: true
            };
